SELECT DISTINCT z.name AS pizzeria_name FROM person_order AS po JOIN person AS p ON po.person_id=p.id JOIN menu AS m ON po.menu_id=m.id JOIN pizzeria AS z ON m.pizzeria_id=z.id WHERE p.gender='female' EXCEPT SELECT DISTINCT z.name FROM person_order AS po JOIN person AS p ON po.person_id=p.id JOIN menu AS m ON po.menu_id=m.id JOIN pizzeria AS z ON m.pizzeria_id=z.id WHERE p.gender='male' UNION SELECT DISTINCT z.name FROM person_order AS po JOIN person AS p ON po.person_id=p.id JOIN menu AS m ON po.menu_id=m.id JOIN pizzeria AS z ON m.pizzeria_id=z.id WHERE p.gender='male' EXCEPT SELECT DISTINCT z.name FROM person_order AS po JOIN person AS p ON po.person_id=p.id JOIN menu AS m ON po.menu_id=m.id JOIN pizzeria AS z ON m.pizzeria_id=z.id WHERE p.gender='female' ORDER BY pizzeria_name;