SELECT p.name FROM person AS p JOIN person_order AS po ON p.id=po.person_id JOIN menu AS m ON po.menu_id=m.id WHERE p.gender='female' GROUP BY p.name HAVING SUM(CASE WHEN m.pizza_name='cheese pizza' THEN 1 ELSE 0 END) >0 AND SUM(CASE WHEN m.pizza_name='pepperoni pizza' THEN 1 ELSE 0 END) >0 ORDER BY p.name;